        :visible.sync="showVoteDialog"
        width="50%"
      >
        <div class="dialog-content">
          <div>How many $WAG do you want to vote?</div>
          
          <el-input v-model="voteAmount" placeholder="Enter amount"></el-input>
  
          <el-button @click="submitVote">Confirm</el-button>
        </div>
      </el-dialog>
    </div>
  </template>
  
